option.value:
import React from "react";

interface ImageSrcWrapperProps {
  /** text shown over the image in the bottom-left */
  overlayText?: string;
  /** optional extra classes for the overlay container */
  overlayClassName?: string;
  /** optional extra classes for the outer wrapper */
  wrapperClassName?: string;
  /** children (parent provides the Image) */
  children?: React.ReactNode;
}

/**
 * ImageSrcWrapper
 * - does NOT render the Image; parent should pass an Image as children
 * - overlayText (optional) will be rendered bottom-left, small & semi-transparent
 */
export default function ImageSrcWrapper({
  overlayText,
  overlayClassName,
  wrapperClassName,
  children,
}: ImageSrcWrapperProps) {
  return (
    // inline-block keeps image display/flow unchanged while allowing absolute overlay
    <div className={`relative inline-block ${wrapperClassName ?? ""}`}>
      {children}

      {overlayText ? (
        <span
          className={
            // small, semi-transparent text with subtle background; non-interactive so clicks pass through to the image
            `absolute left-2 bottom-2 text-xs text-white/80 bg-black/25 px-1 py-0.5 rounded pointer-events-none ` +
            (overlayClassName ? ` ${overlayClassName}` : "")
          }
        >
          {overlayText}
        </span>
      ) : null}
    </div>
  );
}

